import { createContext,useContext, useEffect, useSate } from "react";

//tao mot context moi
const ShoppingContext = createContext();

//component Provider cung cap du lieu cho cac component con 
export const ShoppingContextProvider = ({children}) => {
    const [cartItems, setCartItems] = useSate(() =>{
        const jsonCartData = localStorage.getItem('shopping_cart');
        return jsonCartData ? JSON.parse(jsonCartData) : [];
    });

    useEffect(() =>{
        localStorage.setItem('shopping_cart', JSON.stringify(cartItems));
    }, [cartItems]);

    const cartQty = cartItems.reduce((qty, item) => qty + item.qty, 0);

    const totalPrice = cartItems.reduce((total, item) => total + item.qty * item.price, 0);

    //ham tinh toan tang so luong san pham trong gio hang
    const increaseQty = (_id) => {
        const currentCartItem = cartItems.find(item => item._id === _id);
        if(currentCartItem){
            const newItems = cartItems.map(item => {
                if(item._id === _id){
                    return {...item, qty: item.qty + 1};
                }else {
                    return item;
                }
            });
            setCartItems(newItems);
        }
    };

    //ham giam so luong san pham trong gio hang
    const decreaseQty = (_id) => {
        const currentCartItem = cartItems.find(item => item._id === _id);
        if(currentCartItem) {
            if (currentCartItem.qty === 1){
                // eslint-disable-next-line no-undef
                removeCartItem(_id);
            } else {
                const newItems = cartItems.map(item => {
                    if (item._id === _id){
                        return {...item, qty: item.qty -1};
                    }else {
                        return item;
                    }
                })
            }
        }
    };

    //ham them sp vao gio hang
    const addCartItem = (product) => {
        const currentCartItem = cartItems.find(item => item._id === product._id);
        if(currentCartItem) {
            const newItems = cartItems.map(item => {
                if(item._id === product._id) {
                    return {...item, qty: item.qty +1};
                }else{
                    return item;
                }
            });
            setCartItems(newItems);
        } else {
            const newItems = {...product, qty: 1};
            setCartItems([...cartItems, newItems]);
        }
    };

    //ham xoa sp khoi gio hang
    const removeCartItem = (_id) => {
        const newItems = cartItems.filter(item => item._id !== _id);
        setCartItems(newItems);
    };

    //ham xoa toan bo sp trong gio hang
    const clearCart =() => {
        setCartItems([]);
    };

    //tra ve Provider cua ShoppingContext voi cac gia tri va ham xu ly
    //Component Provider cua Context, cung cap cac gia tri va ham xu ly cho cac component con ben trong
    return(
        <ShoppingContext.Provider value={{cartItems, cartQty, totalPrice, increaseQty, decreaseQty, addCartItem, removeCartItem, clearCart}}>
            {children}
        </ShoppingContext.Provider>
    );
};
export default ShoppingContext;
